mapper.sql:
	 from member 

   WHERE 1=1

      AND memberDelNy = #{shDelNy}



			AND memberRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND memberModDate BETWEEN #{shDateStart} AND #{shDateEnd}



         	 AND memberSeq = #{shValue}
            AND memberName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
		update member
		set
			memberName = #{memberName}
            ,memberId = #{memberId}
            ,memberPwd = #{memberPwd}
         	,memberEmail = #{memberEmail}
         	,genderCd = #{genderCd}
         	,birthDay = #{birthDay}
         	,phoneNumber = #{phoneNumber}
    		where 1=1
    		and memberSeq = #{memberSeq}

mapper.delete:
delete from member
where 1 = 1
	and memberSeq = #{memberSeq}
